import React, { Component } from "react";
import contactArray from "./../contacts.json";

import ArticlesTable from "./ArticlesTable.js";

import "./FilterNav.css";

class FilterNav extends Component {
  constructor(props) {
    super(props);

    this.state = {
      filteredArray: [],
      filter: ""
    };
  }

  componentDidMount() {
    window.scrollTo(0, 0);
    this.setState({
      filteredArray: this.props.contactArray
    });
  }

  // handler = evt => {
  //   const check = evt.target.getAttribute("data-is");
  //   console.log("CHECK", check);

  //   const selection = contactArray.filter(oneContact => {
  //     return oneContact.filter === check;
  //   });

  //   console.log("SELECTION", selection);
  //   this.setState({
  //     dataFilter: selection
  //   });
  // };

  componentDidUpdate(oldProps) {
    if (
      oldProps.productArray.length === 0 &&
      this.props.productArray.length > 0
    ) {
      this.setState({
        filteredArray: this.props.contactArray
      });
    }
  }

  handleChangeType = event => {
    const { value } = event.target;
    let typeArray;
    if (value === "women") {
      typeArray = this.props.contactArray.filter(function(product) {
        return product.productType === "women";
      });
    } else if (value === "men") {
      typeArray = this.props.contactArray.filter(function(product) {
        return product.productType === "men";
      });
    } else if (value === "other") {
      typeArray = this.props.contactArray.filter(function(product) {
        return product.productType === "other";
      });

      this.setState({ gender: value, filteredArray: typeArray });
    }
  };

  handleChange = event => {
    const { value } = event.target;
    let genderArray;
    if (value === "women") {
      genderArray = this.props.contactArray.filter(function(product) {
        return product.gender === "women";
      });
    } else if (value === "men") {
      genderArray = this.props.contactArray.filter(function(product) {
        return product.gender === "men";
      });
    }

    this.setState({ gender: value, filteredArray: genderArray });
  };

  // genericOnChange(event) {
  //   const { filters } = this.state;
  //   const { name, checked } = event.target;
  //   // METTRE A JOUR FILTERS
  //   filters.filter(oneFilter => {
  //     return oneFilter !== checked;
  //   });

  //   this.setState({
  //     [name]: checked,
  //     filters: filters
  //   });
  // }

  // updateSelection() {
  //   const { filters } = this.state;
  //   const selection = contactArray.filter(oneContact => {
  //     return filters.includes(oneContact.filter);
  //   });
  //   console.log("SELECTION", selection);
  //   this.setState({
  //     selection: selection
  //   });
  // }

  //   const men = contactArray.filter (contact => contact.filter === "men");

  //   const women = contactArray.filter(contact => contact.filter === "women");

  //   const other = contactArray.filter(contact => contact.filter === "other");

  render() {
    const { filteredArray } = this.state;
    return (
      <section className="filter-nav">
        <form>
          <label>
            <input
              type="checkbox"
              // data-is="women"
              // onChange={handler}
              // name="filter"
              checked={this.state.women}
              onChange={this.handleChange}
              name="women"
            />
            women
          </label>
          <label>
            <input
              type="checkbox"
              // data-is="men"
              // onChange={handler}
              // name="filter"
              checked={this.state.men}
              onChange={this.handleChange}
              name="men"
            />
            men
          </label>
          <label>
            <input
              type="checkbox"
              // data-is="other"
              // onChange={handler}
              // name="filter"
              checked={this.state.other}
              onChange={this.handleChange}
              name="other"
            />
            other
          </label>
        </form>

        <ArticlesTable selection={this.state.dataFilter} />

        {/* Ici une div/component avec le nombre de boutons générés en fonction de la taille de la selection (donc avec modulo 9) en fonction du bouton cliqué on slice (0,8) ou (9, 17)ou (18,26)....*/}
      </section>
    );
  }
}

export default FilterNav;
