import "./PageNav.css";

class PageNav extends Component {


  render() {
    const contactArray = this.props.selection;
    var pageFloor = Math.floor(contactArray.length / 9);
    var pageStart = [];
    for (var i = 1; i < pageFloor; i++) {
      pageStart.push(i-1*9);
    }
    const pageEnd = pageStart.map(oneIndex => {
      return oneIndex + 8
    })
    if (contactArray.length / 9 != 0) {
      pageStart.push(pageFloor*9 -1);
      pageEnd.push((pageFloor*9 -1) + (contactArray.length % 9 - 1));
    }
    var pageButtons = [];
    for (var i = 1; i < pageStart.length; i++) {
      pageButtons.push(i-1*9);
    } 



    return (
      <section className="page-nav">


      </section>
    );
  }
}

export default PageNav;
