import React, { Component } from "react";

import Header from "./components/Header.js";
import FilterNav from "./components/FilterNav.js";

import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      women: "",
      men: "",
      other: ""
    };
  }

  // this method will be given to TempInput for updating OUR STATE
  updateFahrenheit(event) {
    const { value } = event.target;
    this.setState({
      women: null,
      men: true,
      other: false
    });
  }

  render() {
    return (
      <section className="App">
        <Header celsiusTemp={this.state.women} />

        <FilterNav
          className="articles-table"
          scale="f"
          temperature={this.state.fahrenheit}
          tempChange={event => this.updateFahrenheit(event)}
        />
      </section>
    );
  }
}

export default App;
