import React, { Component } from "react";

import contactArray from "./../contacts.json";

import SearchResults from "../Pages/SearchResults.js";

import "./Header.css";

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      allGender: [],
      searchFilter: ""
    };
  }

  updateSearch(event) {
    const { value } = event.target;
    this.setState({ searchFilter: value });
    console.log(this.state.searchFilter);
  }

  searchUser() {
    const { searchFilter, allGender } = this.state;
    var allResults = allGender.filter(oneUser => {
      return oneUser.username.indexOf(searchFilter) > -1;
    });
    return allResults;
  }

  render() {
    return (
      <div className="header-menu-container">
        <h2 className="header-menu-text">Header + Menu</h2>
      </div>
    );
  }
}

export default Header;
