import React, { Component } from "react";

import contactArray from "./../contacts.json";

// import SearchResults from "../Pages/SearchResults.js";

import "./Header.css";

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      allGender: [],
      searchFilter: ""
    };
  }

  updateSearch(event) {
    const { value } = event.target;
    this.setState({ searchFilter: value });
    console.log(this.state.searchFilter);
  }

  searchUser() {
    const { searchFilter, allGender } = this.state;
    var allResults = allGender.filter(oneUser => {
      return oneUser.username.indexOf(searchFilter) > -1;
    });
    return allResults;
  }

  render() {
    const { searchFilter } = this.state;
    return (
      <div className="SearchPage header-menu-container">
        {/* <SearchBar
        search={this.state.searchUser}
        searchChange={event => this.updateSearch(event)}
      /> */}

        <div className="searchheader">
          <div className="searchbar">
            <input
              onChange={event => this.updateSearch(event)}
              name="search"
              value={searchFilter}
              type="text"
              placeholder="Search"
              className="searchinput"
            />
          </div>
        </div>
      </div>
    );
  }
}

export default Header;
